---@return BattleSceneInfo
function BattleSceneConfig.Get(name)
    if BattleSceneConfig.data == nil then
        BattleSceneConfig.data = require("Game.Data.Excel.BattleScene")
    end
    local info = BattleSceneConfig.data.Get(name) ---@type BattleSceneInfo
    if info == nil then
        logError(string.format("There is not BattleScene info named %s!", name))
    end
    if isString(info.cameraAngle) then
        info.cameraAngle = Tool.ToVector3(info.cameraAngle)
    end
    if isString(info.cameraOffset) then
        info.cameraOffset = Tool.ToVector3(info.cameraOffset)
    end
    return info
end


