local ListItemRenderer = require("Betel.UI.ListItemRenderer")
---@class Game.Modules.BattleConfig.View.ReportCardItem: Betel.UI.ListItemRenderer
local ReportCardItem = class("Game.Modules.BattleConfig.View.ReportCardItem",ListItemRenderer)

---@param gameObject UnityEngine.GameObject
function ReportCardItem:Ctor(gameObject)
    ReportCardItem.super.Ctor(self,gameObject)
end

---@param unit Game.Modules.Battle.Report.Nodes.UnitNode
function ReportCardItem:UpdateItem(unit, index)
    ReportCardItem.super.UpdateItem(self, unit, index)
    local frameImg = self.gameObject:GetImage()
    frameImg.sprite = Res.LoadSprite(RankFrameUrl[unit.rank])

    local iconImg = self.gameObject:GetImage("Icon")
    iconImg.sprite = Res.LoadSprite(unit.cardInfo.iconUrl)
    iconImg.gameObject:SetActive(true)

    local starBar = self.gameObject:FindChild("StarBar")
    self:SetStar(starBar, unit.star)
    --starBar:SetActive(cardVo.fragmentStoneNum == 0)

    local displayText = self.gameObject:GetText("Display")
    displayText.text = "Lv." .. unit.level
end

---@param starBar UnityEngine.GameObject
function ReportCardItem:SetStar(starBar, starNum)
    for i = 1, starBar.transform.childCount do
        local starImg = starBar.transform:GetChild(i - 1).gameObject:GetImage()
        starImg.sprite = UITools.GetSprite(starImg.gameObject, i <= starNum and 1 or 0)
        starImg.gameObject:SetActive(i <= starNum)
    end
end

function ReportCardItem:OnDestroy()

end

return ReportCardItem