local File = {}


--获取目录
function File.GetDirectory(path)
    return path:match("(.+)/[^/]*%.%w+$")
end

--获取文件名
function File.GetFileName(path)
    return path:match("/([^/]*%.%w+)$")
end

--获取文件名,没有扩展名
function File.GetFileNameWithoutExtension(path)
    local filename = File.GetFileName(path)
    local idx = filename:match(".+()%.%w+$")
    if(idx) then
        return filename:sub(1, idx-1)
    else
        return filename
    end
end

--获取扩展名
function File.getExtension(str)
    return str:match(".+%.(%w+)$")
end

return File